package questionthree;

public class triangle extends shape2D {
private int length;
private int base;
private int height;
private int perimeter;
private int area;
void calculateArea() {
	// TODO Auto-generated method stub
	this.area=base*height;
}
void calculatePerimeter() {
	// TODO Auto-generated method stub
	this.perimeter=(base+height+length);
}

}
